import React, { useEffect, useState } from "react";
import "./SigninForm.css";
import axios from "axios";

const SigninForm = () => {
  const [datas, setData] = useState([
    {
      email: "",
      name: "",
    },
  ]);

  useEffect(() => {
    fetch("https://gorest.co.in/public/v2/users")
      .then((res) => {
        if (res.ok) {
          return res.json();
        }
      })
      .then((jsonRes) => setData(jsonRes));
  });

  function onSubmit() {
    const enteredEmail = document.getElementById("email").value;
    const enteredPassward = document.getElementById("passward").value;
    const len = datas.length;
    for (var i = 0; i < len; i++) {
      if (
        enteredEmail === datas[i].email &&
        enteredPassward === datas[i].passward
      ) {
        alert("Login successfully");
        break;
      }
      if (i == len - 1) {
        window.confirm("login unsuccess");
      }
    }
  }

  return (
    <div id="signinForm">
      <div className="heading ccs">
        <h1>
          Welcome Back <br /> <small>Subtitle text goes here</small>
        </h1>
      </div>
      <form onSubmit={onSubmit}>
        <div className="email ccs">
          <input
            type="text"
            placeholder="Email Address*"
            required="true"
            id="email"
          />
        </div>
        <div className="passward ccs">
          <input
            type="text"
            placeholder="Passward*"
            required="true"
            id="passward"
          />
        </div>
        <div className="loginbtn ccs">
          <input type="submit" value="Login" />
        </div>
        <div className="bottom">
          <div className="bottom__checkBox">
            <input type="checkbox" name="" id="checkBox" />
            <label htmlFor="checkBox">Remember Passward</label>
          </div>
          <div className="bottom__forgetPassward">
            <a>
              <small>Forget Passward ?</small>
            </a>
          </div>
        </div>
      </form>
    </div>
  );
};

export default SigninForm;
